cnx = connector.connect(user="root",database='orcid',password="")
query = "SELECT id FROM info GROUP BY id HAVING COUNT(*) > 1"
cursor = cnx.cursor()
cursor.execute(query)
records = cursor.fetchall()
# cnx.commit()
cnt = 1
for item in records:
    id = item[0]
    sql = "SELECT uuid FROM info WHERE id = \"{}\"".format(id)
    cursor.execute(sql)
    data = cursor.fetchall()
    uid = data[0][0]
    delete_sql = "DELETE FROM info WHERE id = \"{}\" AND uuid != \"{}\"".format(id,uid)
    cursor.execute(delete_sql)
    print("已执行{}次".format(cnt))
    cnt += 1
cnx.commit()
cursor.close()
cnx.close()
